import React from 'react'
import styles from './AboutMeRight.scss'
import Talent from './Talent'
import axios from 'axios'

class AboutMeRight extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      name: "",
      school: "",
      department: "",
      age: ""
    }
    this.fetchData = this.fetchData.bind(this)
    this.changeName = this.changeName.bind(this)
  }
  componentWillMount(){
    this.fetchData(7)
  }
  fetchData(id){
    let url = `http://172.18.13.77:3000/users/${id}`
    console.log('url is')
    console.log(url)
    axios.get(url)
    .then(response => {
      this.changeName(response.data.name)
    }
    )
    .catch(err => console.log(err))
  }
  changeName(name){
    this.setState({
      name: name
    })
  }
  render() {
    return (
      <div className="right-container">
        <div className="user-name">Alice</div>
        <div className="user-state">NCTU CS 22yrs</div>
        <Talent />
      </div>
    )
  }
}

export default AboutMeRight
